import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { Button, Container, Form, FormGroup, Input, Label, Row, Col, Option } from 'reactstrap';
import  Modal  from 'react-modal';
import AppNavbar from './AppNavBar';
import './App.js';

class WorkflowEdit extends Component {

    emptyItem = {
        workflowtype: '',
        tradecenter: '',
        branchcode: '',
        refid: '',
        queue: '',
        productcode: '',
        eventtype: '',
        currency: '',
        amount: '',
        closuretype: '',
        comments: ''
    };

    constructor(props) {
        super(props);
        this.state = {
            item: this.emptyItem
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleToggleChange = this.handleToggleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    async componentDidMount() {
        if (this.props.match.params.id !== 'new') {
            const workflow = await (await fetch(`/workflow/${this.props.match.params.id}`)).json();
            this.setState({item: workflow});
        }
    }

    handleChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        let item = {...this.state.item};
        item[name] = value;
        this.setState({item});
    }

    handleToggleChange(event){
        const target = event.target;
        const value = target.id;
        const name = target.name;
        let item = {...this.state.item};
        item[name]=value;
        this.setState({item});
    }

    async handleSubmit(event) {
        event.preventDefault();
        const {item} = this.state;

        await fetch('/workflow' + (item.id ? '/' + item.id : ''), {
            method: (item.id) ? 'PUT' : 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(item),
        });
        this.props.history.push('/workflow');
    }

    render() {
        const {item} = this.state;
        const title = <h3 style={{ fontWeight: 'bold', color: 'blue'}}>{item.id ? 'Edit Work Order':'New Work Order'}</h3>;

        return( <div>
            <AppNavbar/>
            <Container>
                <div>
                    <Modal
                        isOpen="true"
                        contentLabel="Workflow Modal">
                            {title}
                            <Form onSubmit={this.handleSubmit}>
                                <FormGroup>
                                    <Row>
                                        <Col xs="3">
                                            <Label for="workflowtype">Workflow Type</Label>
                                            <select class="form-select" style={{width: "100%"}}
                                                onChange={(e) => {this.handleChange(e)}}>
